var exec = require("child_process").exec;

var scssdir         = __dirname + '/../public/stylesheets/scss/',
    stylesheetsdir  = __dirname + '/../public/stylesheets/';

exports.compile = function(filename) {
    console.log('sass ' + scssdir + filename + '.scss ' + stylesheetsdir + filename + '.css');
    exec('sass ' + scssdir + filename + '.scss ' + stylesheetsdir + filename + '.css', function (err, stdout, stderr) {
        console.log('SCSS file compiled - ' + filename);
    });
}